import React from "react";
import Logo from "../Logo";
import MenuItem from "../MenuItem";
import ToggleConnection from "../ToggleConnection";
import Home from "../../pages/Home";
import Notifications from "../../pages/Notifications";
import Messages from "../../pages/Messages";
import Profile from "../../pages/Profile";
import Analytics from "../../pages/Analytics";
import AboutUs from "../../pages/AboutUs";

class MenuContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      token: "a",
      morePanelDisplay: false,
    };

    this.toggleMorePanelDisplay = this.toggleMorePanelDisplay.bind(this);
  }

  toggleMorePanelDisplay = () => {
    this.setState({
      morePanelDisplay: !this.state.morePanelDisplay,
    });
  };

  displayMoreItems() {
    if (!this.state.morePanelDisplay)
      return (
        <MenuItem
          key={4}
          icon="fa-angles-down"
          title="More"
          onClick={this.toggleMorePanelDisplay}
        />
      );

    return [
      <MenuItem
        key={4}
        icon="fa-chart-line"
        title="Analytics"
        onClick={() =>
          this.props.setMainContainer(
            <Analytics setPage={this.props.setPage} />
          )
        }
      />,

      <MenuItem
        key={5}
        icon="fa-circle-info"
        title="About us"
        onClick={() =>
          this.props.setMainContainer(<AboutUs setPage={this.props.setPage} />)
        }
      />,

      <MenuItem
        key={6}
        icon="fa-angles-up"
        title="Less"
        onClick={this.toggleMorePanelDisplay}
      />,
    ];
  }

  render() {
    let items = [];
    if (this.state.token) {
      items.push(
        <MenuItem
          key={0}
          title="Home"
          icon="fa-house"
          onClick={() =>
            this.props.setMainContainer(
              <Home setMainContainer={this.props.setMainContainer} />
            )
          }
        />
      );

      items.push(
        <MenuItem
          key={1}
          title="Notifications"
          icon="fa-bell"
          onClick={() =>
            this.props.setMainContainer(
              <Notifications setMainContainer={this.props.setMainContainer} />
            )
          }
        />
      );

      items.push(
        <MenuItem
          key={2}
          title="Messages"
          icon="fa-envelope"
          onClick={() =>
            this.props.setMainContainer(
              <Messages setMainContainer={this.props.setMainContainer} />
            )
          }
        />
      );

      items.push(
        <MenuItem
          key={3}
          title="Profile"
          icon="fa-user"
          onClick={() =>
            this.props.setMainContainer(
              <Profile setMainContainer={this.props.setMainContainer} />
            )
          }
        />
      );

      items.push(this.displayMoreItems());
    }

    return (
      <aside className="menu">
        <Logo />
        <div className="items">{items}</div>
        <ToggleConnection />
      </aside>
    );
  }
}

export default MenuContainer;
