import React from "react";
import MonthStatsContainer from "../stats/MonthStatsContainer";
import { getUserStats } from "../../data/data";
import StatsHeader from "../stats/StatsHeader";

const AnalyticsContainer = () => {
  const stats = getUserStats("1");

  const monthsStats = [];

  for (const month in stats) {
    monthsStats.push(
      <MonthStatsContainer key={month} month={month} stats={stats[month]} />
    );
  }

  return (
    <section className="central-container">
      <StatsHeader stats={stats} />
      {monthsStats}
    </section>
  );
};

const StatItem = (props) => {
  return (
    <div className="stat-item">
      <div className="stat-name">{props.stat}</div>
      <div className="stat-value">{props.data}</div>
    </div>
  );
};

export default AnalyticsContainer;
