            setMainContainer={this.props.setMainContainer}
            publish={this.publish}
          />
        )}

        <MessagesList
          messages={this.state.messages}
          setMainContainer={this.props.setMainContainer}
        />
      </section>
    );
  }

  publish(message) {
    // push to server
    this.setState({
      messages: [message, ...this.state.messages],
    });
  }
}

export default FeedContainer;
