import React from "react";
import useLoginForm from "../authentification/useLoginForm";

const handleChange = (event) => {
  console.log(event.target.value);
};

class LoginContainer extends React.Component {
  //   const { handleChange, handleFormSubmit, values, errors } = useLoginForm(
  //     submitForm
  //   );

  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",

      errors: {
        email: "",
        password: "",
      },
    };

    this.setValue = this.setValue.bind(this);
  }

  setValue(name, value) {
    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <form className="auth-wrapper">
        <h2 className="title">Connection</h2>

        <Input
          label="Email"
          name="email"
          type="email"
          handleChange={(e) => this.setValue("email", e.target.value)}
        />

        {/* {errors.email && <p className="errors">{errors.email}</p>} */}

        <Input
          label="Password"
          name="password"
          type="password"
          handleChange={(e) => this.setValue("password", e.target.value)}
        />

        {/* onClick={handleFormSubmit} */}
        <button className="submit">Login</button>

        <div className="move-to-login">
          Already have an account? <a>Login now</a>
        </div>
      </form>
    );
  }
}

const Input = (props) => {
  return (
    <div className="input-container">
      <label className="label">{props.label}</label>
      <input
        className="input"
        type={props.type}
        name={props.name}
        onChange={props.handleChange}
      />
      {error && <p className="errors">{error}</p>}
    </div>
  );
};

export default LoginContainer;
