import React from "react";
import Logo from "../Logo";
import MenuItem from "../MenuItem";
import ToggleConnection from "../ToggleConnection";
import getMorePanelItems from "../../utils/MorePanel";

class MenuContainer extends React.Component {
  constructor(props) {
    super(props);
    this.posRef = React.createRef();

    this.state = {
      token: "a",
      setPage: props.setPage,
      morePanelDisplay: false,
    };
  }

  displayMoreItems() {
    if (this.state.morePanelDisplay)
      return (
        <MenuItem
          icon="fa-angles-down"
          title="More"
          onClick={this.toggleMorePanelDisplay}
        />
      );

    const items = getMorePanelItems();
    items.push(
      <MenuItem
        icon="fa-angles-up"
        title="Less"
        onClick={this.toggleMorePanelDisplay}
      />
    );
    return items;
  }

  render() {
    let items = [];
    if (this.state.token) {
      items.push(<MenuItem key={0} title="Home" icon="fa-house" />);
      items.push(<MenuItem key={1} title="Notifications" icon="fa-bell" />);
      items.push(<MenuItem key={2} title="Messages" icon="fa-envelope" />);
      items.push(<MenuItem key={3} title="Profile" icon="fa-user" />);
      items.push(this.displayMoreItems());
    }

    return (
      <aside className="menu">
        <Logo />
        <div className="items">{items}</div>
        <ToggleConnection />
      </aside>
    );
  }
}

export default MenuContainer;
