import React from "react";
import Logo from "../Logo";
import MenuItem from "../MenuItem";
import ToggleConnection from "../ToggleConnection";
import Home from "../../pages/Home";
import Notifications from "../../pages/Notifications";
import Messages from "../../pages/Messages";
import Profile from "../../pages/Profile";

class MenuContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      token: "a",
      morePanelHide: true,
    };

    this.toggleMorePanelDisplay = this.toggleMorePanelDisplay.bind(this);
  }

  toggleMorePanelDisplay = () => {
    this.setState({
      morePanelHide: !this.state.morePanelHide,
    });
  };

  displayMoreItems() {
    if (this.state.morePanelHide)
      return (
        <MenuItem
          key={4}
          icon="fa-angles-down"
          title="More"
          onClick={this.toggleMorePanelDisplay}
        />
      );

    return [
      <MenuItem
        key={4}
        icon="fa-chart-line"
        title="Analytics"
        onClick={() =>
          this.props.setPage(<Home setPage={this.props.setPage} />)
        }
      />,
      <MenuItem
        key={5}
        icon="fa-circle-info"
        title="About us"
        onClick={() =>
          this.props.setPage(<Home setPage={this.props.setPage} />)
        }
      />,
      <MenuItem
        key={6}
        icon="fa-angles-up"
        title="Less"
        onClick={this.toggleMorePanelDisplay}
      />,
    ];
  }

  render() {
    let items = [];
    if (this.state.token) {
      items.push(
        <MenuItem
          key={0}
          title="Home"
          icon="fa-house"
          onClick={() =>
            this.props.setPage(<Home setPage={this.props.setPage} />)
          }
        />
      );

      items.push(
        <MenuItem
          key={1}
          title="Notifications"
          icon="fa-bell"
          onClick={() =>
            this.props.setPage(<Notifications setPage={this.props.setPage} />)
          }
        />
      );

      items.push(
        <MenuItem
          key={2}
          title="Messages"
          icon="fa-envelope"
          onClick={() =>
            this.props.setPage(<Messages setPage={this.props.setPage} />)
          }
        />
      );

      items.push(
        <MenuItem
          key={3}
          title="Profile"
          icon="fa-user"
          onClick={() =>
            this.props.setPage(<Profile setPage={this.props.setPage} />)
          }
        />
      );

      items.push(this.displayMoreItems());
    }

    return (
      <aside className="menu">
        <Logo />
        <div className="items">{items}</div>
        <ToggleConnection />
      </aside>
    );
  }
}

export default MenuContainer;
