import React from "react";
import ImagePickerContainer from "../ImagePickerContainer";
import dateFormat from "dateformat";
import Icon from "../Icon";
import moment from "moment";
import ProfileContainer from "../containers/ProfileContainer";
import Input from "../Input";

class EditProfile extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      user: props.user,
      editDate: false,
      day: null,
      month: null,
      year: null,
    };

    this.getImage = this.getImage.bind(this);
    this.editUserInformations = this.editUserInformations.bind(this);
    this.toggleEditDate = this.toggleEditDate.bind(this);
    this.editDateInformations = this.editDateInformations.bind(this);
    this.editDate = this.editDate.bind(this);
  }

  getImage(base64) {
    this.editUserInformations("avatar", base64);
  }

  editUserInformations(attirbut, value) {
    this.setState({
      user: {
        ...this.state.user,
        [attirbut]: value,
      },
    });
  }

  editDateInformations(attirbut, value) {
    this.setState({
      [attirbut]: value,
    });
  }

  toggleEditDate() {
    this.setState({
      editDate: !this.state.editDate,
    });
  }

  editDate() {
    const day = this.state.day;
    const month = this.state.month;
    const year = this.state.year;

    if (!day || !month || !year) return;

    let birthday = moment(`${year}-${month}-${day}`, "YYYY-MM-DD");

    if (!birthday.isValid()) {
      alert("Birthday is not valid");
      return;
    }

    birthday = birthday.toDate();

    if (birthday.getTime() > Date.now()) {
      alert("Birthday can't be in the future");
      return;
    }

    this.editUserInformations("birthday", birthday);
    this.toggleEditDate();
  }

  render() {
    const user = this.state.user;

    return (
      <div className="edit-profile">
        <div
          className="btn edit-save"
          onClick={() => {
            // save the user
            this.props.setMainContainer(
              <ProfileContainer
                user={this.state.user}
                setMainContainer={this.props.setMainContainer}
              />
            );

            this.props.close();
          }}
        >
          Save
        </div>
        <div className="edit-avatar">
          <img className="user-avatar" src={user.avatar} alt="avatar" />

          <ImagePickerContainer
            getImage={this.getImage}
            icon="fa-camera-rotate"
          />
        </div>
        <Input label="Name" type="text" value={user.name} />
        <Input label="Email" type="text" value={user.email} />
        <Input label="Bio" type="text" value={user.bio} />
        <Input label="Location" type="text" value={user.location} />
        <Input label="Birthday" type="date" value={user.birthday} />
      </div>
    );
  }
}

export default EditProfile;
