import React from "react";
import Icon from "../Icon";

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      type: this.props.type,
    };
  }

  tooglePassword = () => {
    this.setState({
      type: this.state.type === "password" ? "text" : "password",
    });
  };

  render() {
    return (
      <div className="input-container">
        <div className="input-name">
          <Icon name={this.props.icon} size="fa-xl" />
          <span>{this.props.label}</span>
        </div>
        <input
          className="input"
          type={this.state.type}
          name={this.props.name}
          onChange={this.props.handleChange}
        />
        {this.props.hasShowPassword && (
          <ShownPassword tooglePassword={this.tooglePassword} />
        )}
        {this.props.error && <p className="auth-errors">{this.props.error}</p>}
      </div>
    );
  }
}

const ShownPassword = (props) => {
  return <Icon name="fa-eye" size="fa-xl" />;
  // <Icon name="fa-eye-slash" size="fa-xl" />
};

export default Input;
