import React from "react";

const Input = (props) => {
  return (
    <div className="input-container">
      <div>
        <span>{props.label}</span>
      </div>
      <input
        className="input"
        type={props.type}
        name={props.name}
        onChange={props.handleChange}
      />
      {props.error && <p className="auth-errors">{props.error}</p>}
    </div>
  );
};

export default Input;
