import avatar from "../assets/images/avatar.jpg";
import a from "../assets/images/a.jpg";
import b from "../assets/images/b.jpg";
import c from "../assets/images/c.png";
import d from "../assets/images/d.png";
import dateFormat from "dateformat";

let userIdCpt = 0;
let messageIdCpt = 0;

const names = [
  "Amayas",
  "Hamid",
  "Rayane",
  "Amine",
  "Julien",
  "John",
  "Jane",
  "Cylia",
  "Julia",
  "Marie",
  "Lucas",
  "Lucie",
];

const avatars = [avatar, a, b, c, d];

const bios = [
  "Hello world",
  "J'ai rien d'spécial",
  "Never give up on something that you can’t go a day without thinking about",
];

const generateUser = () => {
  const name = names[Math.floor(Math.random() * names.length)];
  let uid;
  let id = `${userIdCpt++}`;

  let followers = [];
  for (let i = 0; i < Math.floor(Math.random() * 12); ) {
    uid = `${Math.floor(Math.random() * 12)}`;

    if (uid !== id) {
      followers.push(uid);
      i++;
    }
  }

  let following = [];
  for (let i = 0; i < Math.floor(Math.random() * 12); ) {
    uid = `${Math.floor(Math.random() * 12)}`;

    if (uid !== id) {
      following.push(uid);
      i++;
    }
  }

  return {
    id: id.toString(),
    avatar: avatars[Math.floor(Math.random() * avatars.length)],
    name: `${name}`,
    username: `@${name.toLowerCase()}${id}`,
    email: `${name.toLowerCase()}@gmail.com`,
    password: "123456",
    bio: bios[Math.floor(Math.random() * bios.length)],
    birthday: "29/05/2001",
    joinedDate: "01/06/2019",
    followers: followers,
    following: following,
  };
};

const generateStats = () => {
  return {
    "number of profile visits": Math.floor(Math.random() * 100),
    "number of messages you have published": Math.floor(Math.random() * 100),
    "number of messages you have commented on": Math.floor(Math.random() * 100),
    "number of messages you have liked": Math.floor(Math.random() * 100),
    "number of likes that your messages have accumulated": Math.floor(
      Math.random() * 100
    ),
  };
};

const generateMessages = (user) => {
  const _messages = [];

  for (let i = 0; i < Math.floor(Math.random() * 10) + 2; i++) {
    const img =
      Math.random() > 0.6
        ? ""
        : avatars[Math.floor(Math.random() * avatars.length)];

    const date =
      Math.random() > 0.5
        ? dateFormat(new Date(), "dd/mm/yyyy")
        : user.joinedDate;

    let likes = [];
    for (let j = 0; j < Math.floor(Math.random() * userIdCpt); j++)
      likes.push(Math.floor(Math.random() * userIdCpt).toString());

    _messages.push({
      id: `${messageIdCpt++}`,
      content: "Hello world Hey",
      image: img,
      user: user,
      publishDate: date,
      likes: likes,
      isComment: false,
      comments: [],
    });
  }

  return _messages;
};

const users = [
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
  generateUser(),
];

let stats = {};
users.forEach((user) => {
  stats[user.id] = generateStats();
});

const messages = {};
users.forEach((user) => {
  messages[user.id] = generateMessages(user);
});

const getUser = (id) => {
  return users.find((user) => user.id === id);
};

const getUserStats = (id) => {
  return stats[id];
};

const getUserMessages = (id) => {
  return messages[id];
};

const getRandomMessages = () => {
  let allMessages = [];
  users.forEach((user) => {
    allMessages.push(...messages[user.id]);
  });

  allMessages = allMessages
    .sort(() => Math.random() - 0.5)
    .slice(0, Math.random() * allMessages.length);

  return allMessages;
};

export { getUser, getUserStats, getUserMessages, getRandomMessages };
