import React from "react";
import {
  validateEmail,
  validatePassword,
  validatePasswordConfirmation,
} from "../../utils/Validations";
import Form from "../authentification/Form";
import Input from "../authentification/Input";

class SignupContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      username: "",
      email: "",
      password: "",
      passwordconfirmation: "",

      errors: {
        name: "",
        username: "",
        email: "",
        password: "",
        passwordconfirmation: "",
      },
    };

    this.setValue = this.setValue.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  setValue(name, value) {
    this.setState({
      [name]: value,
    });
  }

  handleFormSubmit(e) {
    const {
      name,
      username,
      email,
      password,
      passwordconfirmation,
    } = this.state;

    const nameValidation = validateName(name);
    const usernameValidation = validateUsername(username);
    const emailValidation = validateEmail(email);
    const passwordValidation = validatePassword(password);
    const passwordConfirmationValidation = validatePasswordConfirmation(
      passwordconfirmation,
      password
    );

    this.setState({
      errors: {
        name: nameValidation.message,
        username: usernameValidation.message,
        email: emailValidation.message,
        password: passwordValidation.message,
        passwordconfirmation: passwordConfirmationValidation.message,
      },
    });
  }

  render() {
    return (
      <Form
        title="Create Account"
        action="Sign up"
        moveTo={
          <div className="move-to">
            Already have an account? <span>Login now</span>
          </div>
        }
        handleAction={this.handleFormSubmit}
      >
        <Input
          label="Email"
          name="email"
          type="email"
          handleChange={(e) => this.setValue("email", e.target.value)}
          error={this.state.errors.email}
        />

        <Input
          label="Password"
          name="password"
          type="password"
          handleChange={(e) => this.setValue("password", e.target.value)}
          error={this.state.errors.password}
        />
      </Form>
    );
  }
}

export default SignupContainer;
