import React from "react";
import Message from "../message/Message";
import MinimalUser from "../MinimalUser";
import avatar from "../../assets/images/avatar.jpg";

const u = {
  id: 1,
  avatar: avatar,
  name: "Amayas",
  username: "@sadi",
  bio:
    "Hey bro how are u ? I am fine, and you ? :) what about you ? :) where are you from ? :) Hey bro how are u ? I am fine, and you ? :) what about you ? :) where are you from ? :) Hey bro how are u ? I am fine, and you ? :) what about you ? :) where are you from ? :) Hey bro how are u ? I am fine, and you ? :) what about you ? :) where are you from ? :)",
  birthday: new Date(2001, 4, 29),
  location: "Paris, France",
  joinedDate: new Date(2019, 6, 1),
};

const users = [u, u, u, u, u, u, u, u, u];

const messages = [
  {
    content: "Hey bro",
    image: avatar,
    user: u,
    publishDate: new Date(),
    likes: [],
    comments: [],
    shares: [],
  },

  {
    content: "Hey bro",
    image: avatar,
    user: u,
    publishDate: new Date(),
    likes: [],
    comments: [],
    shares: [],
  },

  {
    content: "Hey bro",
    image: avatar,
    user: u,
    publishDate: new Date(),
    likes: [],
    comments: [],
    shares: [],
  },

  {
    content: "Hey bro",
    image: avatar,
    user: u,
    publishDate: new Date(),
    likes: [],
    comments: [],
    shares: [],
  },
];

class SearchContainer extends React.Component {
  render() {
    return (
      <div className="central-container">
        <span className="search-header"></span>
        <div className="search-results">{this.getResults()}</div>

        {/* {filter === "messages" ? (
          <MessagesResult
            messages={result}
            setMainContainer={this.props.setMainContainer}
          />
        ) : (
          <UsersResult
            users={result}
            setMainContainer={this.props.setMainContainer}
          />
        )} */}
      </div>
    );
  }

  getResults() {
    return <NotFound />;
  }

  search() {
    let query = this.props.query;
    let filter = "messages";

    const regex = /@(\w+)/g;
    if (query.match(regex)) {
      query = query.replace(regex, "$1");
      filter = "users";
    }

    let result = [];
    if (filter === "users") {
      result = users;
    } else {
      result = messages;
    }

    return { filter, result };
  }
}

const SearchUser = (props) => {
  return (
    <li className="user-list-item">
      <MinimalUser
        key={props.index}
        user={props.user}
        setMainContainer={props.setMainContainer}
      />
      <span className="user-list-item-bio">{props.user.bio}</span>
    </li>
  );
};

const MessagesResult = (props) => {
  return (
    <ul className="message-list">
      {props.messages.map((message, index) => (
        <Message
          key={index}
          data={message}
          setMainContainer={props.setMainContainer}
        />
      ))}
    </ul>
  );
};

const UsersResult = (props) => {
  return (
    <ul className="user-list">
      {props.users.map((user, index) => (
        <SearchUser
          key={index}
          user={user}
          setMainContainer={props.setMainContainer}
        />
      ))}
    </ul>
  );
};

const NotFound = (props) => {
  return <h1>Not Found</h1>;
};

export default SearchContainer;
