import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageContent from "./MessageContent";
import MessageActions from "./MessageActions";
import MessageHeader from "./MessageHeader";
import MessageAction from "./MessageAction";
import Message from "./Message";

class MessageSearch extends Message {
  constructor(props) {
    super(props);

    const regex = new RegExp(`(${props.query})`, "gi");

    let content = props.data.content;
    content = content.split(regex);

    let highlightedContent = [];
    for (let s of content) {
      let str = s.trim();
      if (!str) continue;

      if (str.match(regex)) {
        str = str.replace(regex, `<span class="search-highlight">$1</span>`);
      }

      highlightedContent.push(str);
    }

    this.props.data.content = highlightedContent;
    console.log(highlightedContent);
  }

  render() {
    return (
      <article className="message">
        <MessageAvatar
          user={this.props.data.user}
          setMainContainer={this.props.setMainContainer}
        />
        <MessageHeader
          message={this.props.data}
          setMainContainer={this.props.setMainContainer}
        />
        <MessageContent message={this.props.data} />
        <MessageActions>
          <MessageAction name="fa-comment" list={this.props.data.comments} />
          <MessageAction name="fa-heart" list={this.props.data.likes} />
          <MessageAction name="fa-share" list={this.props.data.shares} />
        </MessageActions>
      </article>
    );
  }
}

export default MessageSearch;
