import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageActions from "./MessageActions";
import Icon from "../Icon";
import { ImagePicker } from "react-file-picker";
import ImageHolder from "./ImageHolder";
import Picker from "emoji-picker-react";

class PublishMessage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      image: "",
      content: "",
      showEmojiPicker: true,
    };

    this.setState = this.setState.bind(this);
    this.onEmojiClick = this.onEmojiClick.bind(this);

    this.emojiRectRef = React.createRef();
  }

  onEmojiClick(event, emojiObject) {
    console.log("onEmojiClick");
    this.setState({
      content: this.state.content + emojiObject.emoji,
      showEmojiPicker: false,
    });
  }

  render() {
    return (
      <article className="message publish">
        <MessageAvatar user={this.props.user} />
        <div className="message-content">
          <div
            className="message-input"
            contentEditable="true"
            spellCheck="false"
            data-placeholder="What's happening?"
          >
            {this.state.content}
          </div>
          {this.state.image && <ImageHolder image={this.state.image} />}
        </div>

        <MessageActions hasButton={true}>
          <Icon
            name="fa-face-smile"
            size="fa-xl"
            ref={this.emojiRectRef}
            onClick={() => {
              this.setState({ showEmojiPicker: true });
            }}
          />

          {this.state.showEmojiPicker && (
            <Picker onEmojiClick={this.props.onEmojiClick} />
          )}

          <ImagePickerContainer setState={this.setState} />
          {this.state.image && (
            <Icon
              name="fa-xmark"
              size="fa-xl"
              onClick={() => {
                this.setState({ image: "" });
              }}
            />
          )}
        </MessageActions>
      </article>
    );
  }
}

class EmojiPickerContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showEmojiPicker: true,
      top: 0,
      left: 0,
    };
  }

  render() {
    return this.state.showEmojiPicker ? (
      <div
        className="picker"
        style={{
          top: this.state.top,
          left: this.state.left,
        }}
        onBlur={() => {
          this.setState({ showEmojiPicker: false });
        }}
      >
        <Picker
          onEmojiClick={this.props.onEmojiClick}
          pickerStyle={{ position: "fixed", top: 20 }}
        />
      </div>
    ) : null;
  }

  componentDidMount() {
    let rect = this.props.emojiRectRef.current.getBoundingClientRect();
    let top = rect.top + 5;
    let left = rect.left + 15;

    this.setState({ top: top, left: left });
  }
}

const ImagePickerContainer = (props) => {
  return (
    <ImagePicker
      extensions={["jpg", "jpeg", "png"]}
      onChange={(base64) => {
        props.setState({ image: base64 });
      }}
      dims={{
        minWidth: 0,
        maxWidth: 40000,
        minHeight: 0,
        maxHeight: 40000,
      }}
      onError={(errMsg) => {
        alert(errMsg);
      }}
    >
      <button className="icon fa-solid fa-image fa-xl hide"></button>
    </ImagePicker>
  );
};

export default PublishMessage;
