import React from 'react';

const validation = (values) => {

    let errors={};
    if(!values.fullname){
        errors.fullname="Name is required."
    }

    if(!values.email){
        errors.email="Email is required."
    }else if (!/\S+@\S+\.\S+/.test(values.email)){
        errors.email="Email is invalid."
    }

    const password = values.password;

    const uppercaseRegExp   = /(?=.*?[A-Z])/;
    const lowercaseRegExp   = /(?=.*?[a-z])/;
    const digitsRegExp      = /(?=.*?[0-9])/;
    const specialCharRegExp = /(?=.*?[#?!@$%^&*-])/;
    const minLengthRegExp   = /(.{8,})/;



    if(!password){
        errors.password="Password is required.";
    }else if(! minLengthRegExp.test(password)){
        errors.password="Password must have more than seven characters. ";
    }else if(! uppercaseRegExp.test(password)){
        errors.password="Password must have at least one upperCase";
    }else if(! lowercaseRegExp.test(password)){
        errors.password="Password must have at least one lowerrCase";
    }else if(! digitsRegExp.test(password)){
        errors.password="Password must have at least one digit";
    }else if(! specialCharRegExp.test(password)){
        errors.password="Password must have at least one special";
    }

    const passwordConfirmation = values.passwordconfirmation;

    if(! passwordConfirmation){
        errors.passwordconfirmation="password confirmation is required.";
    }else if( passwordConfirmation !== password){
        errors.passwordconfirmation="password confirmation is not matched."
    }

    return errors;
};

export default validation;
