import React from "react";
import ImagePickerContainer from "../ImagePickerContainer";
import ProfileContainer from "../containers/ProfileContainer";
import Input from "../Input";
import dateFormat from "dateformat";
import moment from "moment";

import {
  validateEmail,
  validatePassword,
  validateName,
  validateDate,
} from "../../utils/Validations";

class EditProfile extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      user: props.user,

      errors: {
        name: "",
        email: "",
        password: "",
        birthday: "",
      },
    };

    this.getImage = this.getImage.bind(this);
    this.editUserInformations = this.editUserInformations.bind(this);
  }

  getImage(base64) {
    this.editUserInformations("avatar", base64);
  }

  editUserInformations(attirbut, value) {
    this.setState({
      user: {
        ...this.state.user,
        [attirbut]: value,
      },
    });
  }

  editDateInformations(attirbut, value) {
    this.setState({
      [attirbut]: value,
    });
  }

  render() {
    const user = this.state.user;

    const birthday = moment(this.state.user.birthday, "DD/MM/YYYY").toDate();

    return (
      <div className="edit-profile">
        <div
          className="btn edit-save"
          onClick={() => {
            const nameValidator = validateName(user.name);
            const emailValidator = validateEmail(user.email);
            const passwordValidator = validatePassword(user.password);
            const birthdayValidator = validateDate(user.birthday);

            const errors = {
              name: nameValidator.message,
              email: emailValidator.message,
              password: passwordValidator.message,
              birthday: birthdayValidator.message,
            };

            this.setState({
              errors: errors,
            });

            // save the user
            this.props.setMainContainer(
              <ProfileContainer
                user={this.state.user}
                setMainContainer={this.props.setMainContainer}
              />
            );

            if (
              nameValidator.valid &&
              emailValidator.valid &&
              passwordValidator.valid &&
              birthdayValidator.valid
            )
              this.props.close();
          }}
        >
          Save
        </div>
        <div className="edit-avatar">
          <img className="user-avatar" src={user.avatar} alt="avatar" />

          <ImagePickerContainer
            getImage={this.getImage}
            icon="fa-camera-rotate"
          />
        </div>

        <Input
          name="name"
          label="Name"
          icon="fa-signature"
          type="text"
          value={user.name}
          error={this.state.errors.name}
          handleChange={(event) =>
            this.editUserInformations("name", event.target.value)
          }
        />

        <Input
          name="email"
          label="Email"
          icon="fa-envelope"
          type="email"
          value={user.email}
          error={this.state.errors.email}
          handleChange={(event) =>
            this.editUserInformations("email", event.target.value)
          }
        />

        <Input
          name="bio"
          label="Bio"
          icon="fa-id-card"
          type="text"
          value={user.bio}
          handleChange={(event) =>
            this.editUserInformations("bio", event.target.value)
          }
        />

        <Input
          name="birthday"
          label="Birthday"
          icon="fa-cake-candles"
          type="date"
          value={dateFormat(birthday, "yyyy-mm-dd")}
          error={this.state.errors.birthday}
          handleChange={(event) => {
            console.log(event.target.value);
            birthday = moment(event.target.value, "YYYY-MM-DD").toDate();
            birthday = dateFormat(birthday, "mm/dd/yyyy");
            this.editUserInformations("birthday", birthday);
          }}
        />

        <Input
          name="password"
          label="Password"
          icon="fa-key"
          type="password"
          value={user.password}
          hasShowPassword={true}
          error={this.state.errors.password}
          handleChange={(event) =>
            this.editUserInformations("password", event.target.value)
          }
        />
      </div>
    );
  }
}

export default EditProfile;
