import React from "react";
import Action from "../Action";

const UserBanner = (props) => {
  return (
    <div className="user-banner-container">
      <div className="user-header">
        <img
          src={props.user.avatar}
          alt="avatar"
          className="user-banner-avatar"
        />

        <div className="user-banner-name">
          <span>{props.user.name}</span>
          <span>{props.user.username}</span>
        </div>
      </div>

      <div className="user-metadata">
        <span>{props.user.bio}</span>

        <div className="user-num-infos">
          <UserMetadata name="fa-location-dot" data="Paris, France" />
          <UserMetadata name="fa-cake-candles" data="Born May 29, 2001" />
          <div className="user-num-info">
            <Action name="fa-cake-candles" size="fa-lg" />
            <span>Born May 29, 2001</span>
          </div>

          <div className="user-num-info">
            <Action name="fa-calendar-days" size="fa-lg" />
            <span>Joined June 1, 2019</span>
          </div>
        </div>

        <div className="user-stats">
          <div className="user-stat">
            <span>10</span>
            <span>Following</span>
          </div>

          <div className="user-stat">
            <span>10</span>
            <span>Followers</span>
          </div>
        </div>
      </div>
    </div>
  );
};

const UserMetadata = (props) => {
  return (
    <div className="user-metadata">
      <Action name={props.name} size="fa-lg" />
      <span>{props.data}</span>
    </div>
  );
};

export default UserBanner;
