import React from "react";
import useSignupForm from "../authentification/useSignupForm";
const SignupContainer = ({ submitForm }) => {
  const { handleChange, handleFormSubmit, values, errors } = useSignupForm(
    submitForm
  );

  return (
    <div className="container">
      <div className="auth-wrapper">
        <h2 className="title">Create Account</h2>

        <form className="form-wrapper">
          <div className="name">
            <label className="label">Full Name</label>
            <input
              className="input"
              type="text"
              name="fullname"
              value={values.fullname}
              onChange={handleChange}
            />
            {errors.fullname && <p className="errors">{errors.fullname}</p>}
          </div>
          <div className="email">
            <label className="label">Email</label>
            <input
              className="input"
              type="email"
              name="email"
              value={values.email}
              onChange={handleChange}
            />
            {errors.email && <p className="errors">{errors.email}</p>}
          </div>
          <div className="password">
            <label className="label">Password</label>
            <input
              className="input"
              type="password"
              name="password"
              value={values.password}
              onChange={handleChange}
            />
            {errors.password && <p className="errors">{errors.password}</p>}
          </div>
          <div className="password">
            <label className="label">Confirm your password</label>
            <input
              className="input"
              type="password"
              name="passwordconfirmation"
              value={values.passwordconfirmation}
              onChange={handleChange}
            />
            {errors.passwordconfirmation && (
              <p className="errors">{errors.passwordconfirmation}</p>
            )}
          </div>
          <div className="reset-password">
            Forgot your password?<a>Reset it</a>
          </div>
          <div>
            <button className="submit" onClick={handleFormSubmit}>
              Sign up
            </button>
          </div>
          <div className="move-to-login">
            Already have an account? <a>Login now</a>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignupContainer;
