import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageContent from "./MessageContent";
import MessageActions from "./MessageActions";
import MessageHeader from "./MessageHeader";
import MessageAction from "./MessageAction";

class Message extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      content: props.data.content,
      image: props.data.image,
      user: props.data.user,
      publishDate: props.data.publishDate,
      likes: props.data.likes,
      comments: props.data.comments,
      shares: props.data.shares,
    };
  }

  render() {
    return (
      <article className="message">
        <MessageAvatar
          user={this.state.user}
          setMainContainer={this.props.setMainContainer}
        />
        <MessageHeader
          message={this.state}
          setMainContainer={this.props.setMainContainer}
        />
        <MessageContent message={this.state} />
        <MessageActions>
          <MessageAction name="fa-comment" list={this.state.comments} />
          <MessageAction name="fa-heart" list={this.state.likes} />
          <MessageAction name="fa-share" list={this.state.shares} />
        </MessageActions>
      </article>
    );
  }
}

export default Message;
