import moment from "moment";
import React from "react";

const MonthStatsContainer = (props) => {
  const statsItems = [];

  for (let stat in props.stats) {
    statsItems.push(
      <StatItem key={stat} stat={stat} data={props.stats[stat]} />
    );
  }

  const month = moment(props.month).format("MMM, YYYY");

  return (
    <div className="month-stats-container">
      <span className="month-name">{month}</span>
      <div className="stats-list">{statsItems}</div>
    </div>
  );
};

const StatItem = (props) => {
  return (
    <div className="stat-item">
      <div className="stat-name">{props.stat}</div>
      <div className="stat-value">{props.data}</div>
    </div>
  );
};

export default MonthStatsContainer;
