import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageActions from "./MessageActions";
import Icon from "../Icon";
import { ImagePicker } from "react-file-picker";
import ImageHolder from "./ImageHolder";
import EmojiPicker from "emoji-picker-react";

class PublishMessage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      image: "",
    };

    this.setState = this.setState.bind(this);
  }

  render() {
    return (
      <article className="message publish">
        <MessageAvatar user={this.props.user} />
        <div className="message-content">
          <div
            className="message-input"
            contentEditable="true"
            spellCheck="false"
            data-placeholder="What's happening?"
          ></div>
          {this.state.image && <ImageHolder image={this.state.image} />}
        </div>

        <MessageActions hasButton={true}>
          <Icon name="fa-face-smile" size="fa-xl" />
          <EmojiPickerContainer />
          <ImagePickerContainer setState={this.setState} />

          {this.state.image && (
            <Icon
              name="fa-xmark"
              size="fa-xl"
              onClick={() => {
                this.setState({ image: "" });
              }}
            />
          )}
        </MessageActions>
      </article>
    );
  }
}

const EmojiPickerContainer = (props) => {
  return (
    <div
      className="picker"
      // onBlur={() => {
      //   console.log("blur");
      //   let picker = document.querySelector(".picker");
      //   picker.style.visibility = "hidden";
      // }}
      style={{
        // visibility: "hidden",
        position: "fixed",
        backgroundColor: "yellow",
      }}
    >
      <EmojiPicker />
    </div>
  );
};

const ImagePickerContainer = (props) => {
  return (
    <ImagePicker
      extensions={["jpg", "jpeg", "png"]}
      onChange={(base64) => {
        props.setState({ image: base64 });
      }}
      dims={{
        minWidth: 0,
        maxWidth: 40000,
        minHeight: 0,
        maxHeight: 40000,
      }}
      onError={(errMsg) => {
        alert(errMsg);
      }}
    >
      <button className="icon fa-solid fa-image fa-xl hide"></button>
    </ImagePicker>
  );
};

export default PublishMessage;
