import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageActions from "./MessageActions";
import Icon from "../Icon";
import Picker from "emoji-picker-react";

let n = 0;
class PublishMessage extends React.Component {
  constructor(props) {
    super(props);
    this.posRef = React.createRef();
  }

  render() {
    return (
      <article className="message publish">
        <MessageAvatar user={this.props.user} />
        <div className="message-content">
          <div
            className="message-input"
            contentEditable="true"
            spellCheck="false"
            data-placeholder="What's happening?"
          ></div>
        </div>
        <MessageActions hasButton={true}>
          <span
            id="test"
            style={{ color: "white" }}
            onClick={() => {
              let e = document.getElementById("test");

              let picker = document.querySelector(".emoji-picker-react");

              picker.style.top = e.getBoundingClientRect().top + 10 + "px";
              picker.style.left = e.getBoundingClientRect().left + 10 + "px";

              if (n % 2 == 0) picker.style.visibility = "visible";
              else picker.style.visibility = "hidden";

              n++;

              document.body.onclick = (e) => {
                if (
                  !picker.contains(e.target) &&
                  picker.style.visibility == "visible"
                ) {
                  picker.style.visibility = "hidden";
                }
              };
            }}
          >
            Hey
          </span>
          <Icon name="fa-face-smile" size="fa-xl" />
          <Icon name="fa-image" size="fa-xl" />
          <Icon name="fa-square-poll-vertical" size="fa-xl" />
          <Picker pickerStyle={{ visibility: "hidden", position: "fixed" }} />
        </MessageActions>
      </article>
    );
  }
}

export default PublishMessage;
