import React from "react";
import { validateEmail, validatePassword } from "../../utils/Validations";

class LoginContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",

      errors: {
        email: "",
        password: "",
      },
    };

    this.setValue = this.setValue.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  setValue(name, value) {
    this.setState({
      [name]: value,
    });
  }

  handleFormSubmit(e) {
    const { email, password } = this.state;

    const { emailStatus, emailError } = validateEmail(email);
    const { passwordStatus, passwordError } = validatePassword(password);

    this.setState({
      errors: {
        email: emailError,
        password: passwordError,
      },
    });
  }

  render() {
    return (
      <form className="auth-wrapper">
        <h2 className="auth-title">Connection</h2>

        <Input
          label="Email"
          name="email"
          type="email"
          handleChange={(e) => this.setValue("email", e.target.value)}
          error={this.state.errors.email}
        />

        <Input
          label="Password"
          name="password"
          type="password"
          handleChange={(e) => this.setValue("password", e.target.value)}
          error={this.state.errors.password}
        />

        <div className="auth-submit" onClick={this.handleFormSubmit}>
          Login
        </div>

        <div className="move-to-login">
          Already have an account ? <span>Login now</span>
        </div>
      </form>
    );
  }
}

const Input = (props) => {
  return (
    <div className="input-container">
      <span>{props.label}</span>
      <input
        className="input"
        type={props.type}
        name={props.name}
        onChange={props.handleChange}
      />
      {props.error && <p className="auth-errors">{props.error}</p>}
    </div>
  );
};

export default LoginContainer;
