import React from "react";
import EmojiPicker from "emoji-picker-react";

class EmojiPickerContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showEmojiPicker: true,
    };
  }

  render() {
    console.log(this.props);

    return this.state.showEmojiPicker ? (
      <div
        className="picker"
        style={{
          top: "0px",
          left: "0px",
        }}
      >
        <EmojiPicker />
      </div>
    ) : null;
  }
}

export default React.forwardRef((props, ref) => (
  <EmojiPickerContainer emojiRectRef={ref} {...props} />
));
