import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageActions from "./MessageActions";
import Icon from "../Icon";
import Picker from "emoji-picker-react";
import { ImagePicker } from "react-file-picker";

class PublishMessage extends React.Component {
  constructor(props) {
    super(props);
    this.posRef = React.createRef();
  }

  render() {
    return (
      <article className="message publish">
        <MessageAvatar user={this.props.user} />
        <div className="message-content">
          <div
            className="message-input"
            contentEditable="true"
            spellCheck="false"
            data-placeholder="What's happening?"
          ></div>

          <div className="message-image-holder"></div>
        </div>

        <MessageActions hasButton={true}>
          <Icon name="fa-face-smile" size="fa-xl" />

          <ImagePicker
            extensions={["jpg", "jpeg", "png"]}
            onChange={(base64) => {
              let imageHolder = document.querySelector(".message-image-holder");
              imageHolder.innerHTML = `<img src="${base64}" class="message-image-holder message-image-holder-displayed " />`;
            }}
            dims={{
              minWidth: 100,
              maxWidth: 4000,
              minHeight: 100,
              maxHeight: 4000,
            }}
            onError={(errMsg) => {
              console.log(errMsg);
            }}
          >
            <button className="icon fa-solid fa-image fa-xl hide"></button>
          </ImagePicker>

          <Icon name="fa-square-poll-vertical" size="fa-xl" />
          <div
            className="picker"
            onBlur={() => {
              console.log("blur");
              let picker = document.querySelector(".picker");
              picker.style.visibility = "hidden";
            }}
            style={{
              visibility: "hidden",
              position: "fixed",
              backgroundColor: "yellow",
            }}
            autoFocus
          >
            <Picker />
          </div>
        </MessageActions>
      </article>
    );
  }
}

export default PublishMessage;
