import React from "react";
import Logo from "../Logo";
import MenuItem from "../MenuItem";
import ToggleConnection from "../ToggleConnection";
import Home from "../../pages/Home";
import Notifications from "../../pages/Notifications";
import Messages from "../../pages/Messages";
import Profile from "../../pages/Profile";

class MenuContainer extends React.Component {
  constructor(props) {
    super(props);
    this.posRef = React.createRef();

    this.state = {
      token: "a",
      setPage: props.setPage,
      morePanelHide: false,
    };

    this.toggleMorePanelDisplay = this.toggleMorePanelDisplay.bind(this);
  }

  toggleMorePanelDisplay = () => {
    this.setState({
      morePanelHide: !this.state.morePanelHide,
    });
  };

  displayMoreItems() {
    if (this.state.morePanelHide)
      return (
        <MenuItem
          key={4}
          icon="fa-angles-down"
          title="More"
          onClick={this.toggleMorePanelDisplay}
        />
      );

    return [
      <MenuItem key={4} icon="fa-chart-line" title="Analytics" />,
      <MenuItem key={5} icon="fa-circle-info" title="About us" />,
      <MenuItem
        key={6}
        icon="fa-angles-up"
        title="Less"
        onClick={this.toggleMorePanelDisplay}
      />,
    ];
  }

  render() {
    let items = [];
    if (this.state.token) {
      items.push(
        <MenuItem
          key={0}
          title="Home"
          icon="fa-house"
          onClick={() => {
            console.log("Home");
            this.state.setPage(<Home />);
          }}
        />
      );

      items.push(
        <MenuItem
          key={1}
          title="Notifications"
          icon="fa-bell"
          onClick={() => this.state.setPage(<Notifications />)}
        />
      );

      items.push(
        <MenuItem
          key={2}
          title="Messages"
          icon="fa-envelope"
          onClick={() => this.state.setPage(<Messages />)}
        />
      );

      items.push(
        <MenuItem
          key={3}
          title="Profile"
          icon="fa-user"
          onClick={() => this.state.setPage(<Profile />)}
        />
      );

      items.push(this.displayMoreItems());
    }

    return (
      <aside className="menu">
        <Logo />
        <div className="items">{items}</div>
        <ToggleConnection />
      </aside>
    );
  }
}

export default MenuContainer;
