import React from "react";
import useLoginForm from "../authentification/useLoginForm";
const LoginContainer = ({ submitForm }) => {
  const { handleChange, handleFormSubmit, values, errors } = useLoginForm(
    submitForm
  );

  return (
    <form className="auth-wrapper">
      <h2 className="title">Connection</h2>

      <Input
        label="Email"
        name="email"
        type="email"
        handleChange={handleChange}
      />

      {/* value={values.email}
          onChange={handleChange} */}

      {/* {errors.email && <p className="errors">{errors.email}</p>} */}

      <Input
        label="Password"
        name="password"
        type="password"
        handleChange={handleChange}
      />
      {/* <div className="password">
        <label className="label">Password</label>
        <input
          className="input"
          type="password"
          name="password"
          value={values.password}
          onChange={handleChange}
        />
        {errors.password && <p className="errors">{errors.password}</p>}
      </div> */}

      <button className="submit" onClick={handleFormSubmit}>
        Login
      </button>

      <div className="move-to-login">
        Already have an account? <a>Login now</a>
      </div>
    </form>
  );
};

const Input = (props) => {
  return (
    <div className="input-container">
      <label className="label">{props.label}</label>
      <input
        className="input"
        type={props.type}
        name={props.name}
        onChange={props.handleChange}
      />
      {/* {errors.email && <p className="errors">{}</p>} */}
    </div>
  );
};

export default LoginContainer;
