import React from "react";

const MessageActions = (props) => {
  const icons = [];

  for (let i = 0; i < props.actions.length; i++)
    icons.push(<i className={props.actions[i].icon}></i>);

  return (
    <div className="message-actions">
      <div className="actions">
        <em className="action fa-solid fa-comment fa-lg"></em>
        <em className="action fa-solid fa-heart fa-lg"></em>
        <em className="action fa-solid fa-share fa-lg"></em>
      </div>
    </div>
  );
};

export default MessageActions;
