import React from "react";
import MessageHeader from "./MessageHeader";
import MessageActions from "./MessageActions";
import Action from "./Action";

const PublishMessage = (props) => {
  return (
    <article className="message publish">
      <MessageHeader avatar={props.avatar} />
      <div className="message-content">
        <textarea className="input"></textarea>
      </div>
      <MessageActions>
        <Action type="fa-solid" name="fa-face-smile" size="fa-xl" />
        <Action type="fa-solid" name="fa-face-heart" size="fa-xl" />
        <Action type="fa-solid" name="fa-face-share" size="fa-xl" />
        iconsNames=
        {[
          "fa-regular fa-face-smile fa-xl",
          "fa-solid fa-image fa-xl",
          "fa-solid fa-square-poll-vertical fa-xl",
        ]}
      </MessageActions>
    </article>
  );
};

export default PublishMessage;
