import React from "react";
import MessageAvatar from "./MessageAvatar";
import MessageContent from "./MessageContent";
import MessageActions from "./MessageActions";
import MessageHeader from "./MessageHeader";
import MessageAction from "./MessageAction";
import MessageViewContainer from "../containers/MessageViewContainer";
import Popup from "reactjs-popup";
import MessageLikes from "./MessageLikes";
import { getUser } from "../../data/data";
import Icon from "../Icon";
class Message extends React.Component {
  render() {
    let likes = [];
    for (const i of this.props.data.likes) likes.push(getUser(i.toString()));
    const style = {
      width: "700px",
      height: "600px",
      overflowY: "scroll",
      borderRadius: "30px",
      border: "1px solid #f3f4f6",
      backgroundColor: "#111827",
    };

    return (
      <article className="message">
        <MessageAvatar
          user={this.props.data.user}
          setMainContainer={this.props.setMainContainer}
        />
        <MessageHeader
          message={this.props.data}
          setMainContainer={this.props.setMainContainer}
        />
        <MessageContent message={this.props.data} />
        <MessageActions>
          {this.props.data.isComment || (
            <MessageAction
              name="fa-comment"
              list={this.props.data.comments}
              onClick={() => {
                this.props.setMainContainer(
                  <MessageViewContainer
                    message={this.props.data}
                    setMainContainer={this.props.setMainContainer}
                  />
                );
              }}
            />
          )}

          <span className="message-action">
            <Icon name="fa-heart" size="fa-xl" />

            <Popup
              trigger={
                <span className="message-action-text"> {likes.length} </span>
              }
              contentStyle={style}
              modal
              closeOnDocumentClick
              closeOnEscape
              lockScroll={true}
            >
              {(close) => (
                <MessageLikes
                  likes={likes}
                  setMainContainer={this.props.setMainContainer}
                  close={close}
                />
              )}
            </Popup>
          </span>
        </MessageActions>
      </article>
    );
  }
}

export default Message;
